// Enhanced icon creation function with stroke width and fill support
function createIcon(iconName, color = 'slate-500', size = 28, strokeWidth = 1, filled = false) {
    const iconPath = MATERIAL_ICONS[iconName] || MATERIAL_ICONS['link'];
    const colorHex = ENHANCED_COLOR_PALETTE[color] || ENHANCED_COLOR_PALETTE['slate-500'];
    
    const fillAttribute = filled ? `fill="${colorHex}"` : 'fill="none"';
    const strokeAttribute = filled ? 'stroke="none"' : `stroke="${colorHex}" stroke-width="${strokeWidth}"`;
    
    return `
        <svg width="${size}" height="${size}" viewBox="0 0 24 24" ${fillAttribute} ${strokeAttribute} stroke-linecap="round" stroke-linejoin="round">
            ${iconPath}
        </svg>
    `;
}

// Enhanced icon creation with color name support
function createIconWithColor(iconName, colorName = 'slate-500', size = 28, strokeWidth = 1, filled = false) {
    return createIcon(iconName, colorName, size, strokeWidth, filled);
}

// Get color hex value from color name
function getColorHex(colorName) {
    return ENHANCED_COLOR_PALETTE[colorName] || ENHANCED_COLOR_PALETTE['slate-500'];
}

// Get color class for text
function getColorClass(color, type = 'text') {
    if (!color || !color.includes('-')) {
        color = 'slate-500'; // fallback
    }
    
    const [colorName, shade] = color.split('-');
    
    switch (type) {
        case 'text':
            return `text-${colorName}-${shade}`;
        case 'bg':
            return `bg-${colorName}-${shade}`;
        case 'border':
            return `border-${colorName}-${shade}`;
        default:
            return `text-${colorName}-${shade}`;
    }
}

// Populate color picker with enhanced colors
function populateColorPicker(containerId, selectedColor = 'blue-500', callback) {
    const container = document.getElementById(containerId);
    if (!container) return;
    
    container.innerHTML = '';
    
    Object.entries(ENHANCED_COLOR_PALETTE).forEach(([colorKey, colorHex]) => {
        const colorDiv = document.createElement('div');
        colorDiv.className = `color-option ${selectedColor === colorKey ? 'selected' : ''}`;
        colorDiv.style.backgroundColor = colorHex;
        colorDiv.dataset.color = colorKey;
        colorDiv.title = colorKey.split('-').map(word => 
            word.charAt(0).toUpperCase() + word.slice(1)).join(' ');
        
        colorDiv.addEventListener('click', () => {
            // Remove selected class from all options
            container.querySelectorAll('.color-option').forEach(opt => {
                opt.classList.remove('selected');
            });
            
            // Add selected class to clicked option
            colorDiv.classList.add('selected');
            
            // Call callback with selected color
            if (callback) callback(colorKey);
        });
        
        container.appendChild(colorDiv);
    });
}

// Populate icon picker with categories
function populateIconPicker(containerId, selectedIcon = 'link', callback) {
    const container = document.getElementById(containerId);
    if (!container) return;
    
    container.innerHTML = '';
    
    Object.entries(MATERIAL_ICONS).forEach(([iconKey, iconPath]) => {
        const iconDiv = document.createElement('div');
        iconDiv.className = `icon-option ${selectedIcon === iconKey ? 'selected' : ''}`;
        iconDiv.dataset.icon = iconKey;
        iconDiv.title = iconKey.replace(/-/g, ' ').replace(/\b\w/g, l => l.toUpperCase());
        iconDiv.innerHTML = createIcon(iconKey, 'slate-500', 20);
        
        iconDiv.addEventListener('click', () => {
            // Remove selected class from all options
            container.querySelectorAll('.icon-option').forEach(opt => {
                opt.classList.remove('selected');
            });
            
            // Add selected class to clicked option
            iconDiv.classList.add('selected');
            
            // Call callback with selected icon
            if (callback) callback(iconKey);
        });
        
        container.appendChild(iconDiv);
    });
}

